import { Component } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormsModule } from '@angular/forms';
import { RouterModule } from '@angular/router';

@Component({
  selector: 'app-register',
  standalone: true,
  imports: [CommonModule, FormsModule, RouterModule],
  template: `
    <div class="min-h-screen flex items-center justify-center bg-gray-50 py-12 px-4 sm:px-6 lg:px-8">
      <div class="max-w-md w-full space-y-8">
        <!-- Back to home link -->
        <div class="text-left">
          <button 
            class="flex items-center text-gray-600 hover:text-gray-900 transition-colors"
            routerLink="/home">
            <i class="fas fa-arrow-left mr-2"></i>
            Back to home
          </button>
        </div>

        <!-- Logo and Title -->
        <div class="text-center">
          <div class="flex items-center justify-center mb-6">
            <div class="w-12 h-12 bg-primary rounded-lg flex items-center justify-center mr-3">
              <i class="fas fa-calendar-alt text-white text-xl"></i>
            </div>
            <span class="text-2xl font-bold text-gray-900">LeaveFlow</span>
          </div>
        </div>

        <!-- Registration Form Card -->
        <div class="bg-white rounded-2xl shadow-lg p-8 border border-gray-200">
          <div class="card-header">
            <h2 class="card-title">Create your account</h2>
            <p class="card-subtitle">Join your organization's leave management system</p>
          </div>

          <form (ngSubmit)="onSubmit()" #registerForm="ngForm">
            <!-- Name Fields -->
            <div class="grid grid-cols-2 gap-4 mb-4">
              <div class="form-group">
                <label class="form-label">First name</label>
                <input 
                  type="text" 
                  class="form-control"
                  placeholder="John"
                  [(ngModel)]="userData.firstName"
                  name="firstName"
                  required
                  minlength="2">
              </div>
              <div class="form-group">
                <label class="form-label">Last name</label>
                <input 
                  type="text" 
                  class="form-control"
                  placeholder="Doe"
                  [(ngModel)]="userData.lastName"
                  name="lastName"
                  required
                  minlength="2">
              </div>
            </div>

            <!-- Email -->
            <div class="form-group">
              <label class="form-label">Email</label>
              <div class="input-group">
                <i class="input-icon fas fa-envelope"></i>
                <input 
                  type="email" 
                  class="form-control"
                  placeholder="Enter your email"
                  [(ngModel)]="userData.email"
                  name="email"
                  required
                  email>
              </div>
            </div>

            <!-- Password -->
            <div class="form-group">
              <label class="form-label">Password</label>
              <div class="input-group">
                <i class="input-icon fas fa-lock"></i>
                <input 
                  [type]="showPassword ? 'text' : 'password'" 
                  class="form-control"
                  placeholder="Create a password"
                  [(ngModel)]="userData.password"
                  name="password"
                  required
                  minlength="6">
                <button 
                  type="button"
                  class="absolute right-3 top-1/2 transform -translate-y-1/2 text-gray-400 hover:text-gray-600"
                  (click)="togglePassword()">
                  <i [class]="showPassword ? 'fas fa-eye-slash' : 'fas fa-eye'"></i>
                </button>
              </div>
            </div>

            <!-- Confirm Password -->
            <div class="form-group">
              <label class="form-label">Confirm password</label>
              <div class="input-group">
                <i class="input-icon fas fa-lock"></i>
                <input 
                  [type]="showConfirmPassword ? 'text' : 'password'" 
                  class="form-control"
                  placeholder="Confirm your password"
                  [(ngModel)]="userData.confirmPassword"
                  name="confirmPassword"
                  required>
                <button 
                  type="button"
                  class="absolute right-3 top-1/2 transform -translate-y-1/2 text-gray-400 hover:text-gray-600"
                  (click)="toggleConfirmPassword()">
                  <i [class]="showConfirmPassword ? 'fas fa-eye-slash' : 'fas fa-eye'"></i>
                </button>
              </div>
              <div *ngIf="userData.confirmPassword && userData.password !== userData.confirmPassword" 
                   class="text-red-500 text-sm mt-1">
                Passwords do not match
              </div>
            </div>

            <!-- Terms and Privacy -->
            <div class="form-group">
              <label class="flex items-start">
                <input 
                  type="checkbox" 
                  class="mt-1 mr-3 h-4 w-4 text-primary border-gray-300 rounded focus:ring-primary"
                  [(ngModel)]="userData.agreedToTerms"
