import { Component } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormsModule } from '@angular/forms';
import { RouterModule } from '@angular/router';

@Component({
  selector: 'app-login',
  standalone: true,
  imports: [CommonModule, FormsModule, RouterModule],
  template: `
    <div class="min-h-screen flex items-center justify-center bg-gray-50 py-12 px-4 sm:px-6 lg:px-8">
      <div class="max-w-md w-full space-y-8">
        <!-- Back to home link -->
        <div class="text-left">
          <button 
            class="flex items-center text-gray-600 hover:text-gray-900 transition-colors"
            routerLink="/home">
            <i class="fas fa-arrow-left mr-2"></i>
            Back to home
          </button>
        </div>

        <!-- Logo and Title -->
        <div class="text-center">
          <div class="flex items-center justify-center mb-6">
            <div class="w-12 h-12 bg-primary rounded-lg flex items-center justify-center mr-3">
              <i class="fas fa-calendar-alt text-white text-xl"></i>
            </div>
            <span class="text-2xl font-bold text-gray-900">LeaveFlow</span>
          </div>
        </div>

        <!-- Login Form Card -->
        <div class="bg-white rounded-2xl shadow-lg p-8 border border-gray-200">
          <div class="card-header">
            <h2 class="card-title">Welcome back</h2>
            <p class="card-subtitle">Sign in to manage your team's leave requests</p>
          </div>

          <form (ngSubmit)="onSubmit()" #loginForm="ngForm">
            <!-- Email -->
            <div class="form-group">
              <label class="form-label">Email</label>
              <div class="input-group">
                <i class="input-icon fas fa-envelope"></i>
                <input 
                  type="email" 
                  class="form-control"
                  placeholder="Enter your email"
                  [(ngModel)]="credentials.email"
                  name="email"
                  required
                  email>
              </div>
            </div>

            <!-- Password -->
            <div class="form-group">
              <div class="flex justify-between items-center mb-2">
                <label class="form-label">Password</label>
                <a href="#" class="text-primary text-sm font-medium hover:text-primary-dark">
                  Forgot password?
                </a>
              </div>
              <div class="input-group">
                <i class="input-icon fas fa-lock"></i>
                <input 
                  [type]="showPassword ? 'text' : 'password'" 
                  class="form-control"
                  placeholder="Enter your password"
                  [(ngModel)]="credentials.password"
                  name="password"
                  required>
                <button 
                  type="button"
                  class="absolute right-3 top-1/2 transform -translate-y-1/2 text-gray-400 hover:text-gray-600"
                  (click)="togglePassword()">
                  <i [class]="showPassword ? 'fas fa-eye-slash' : 'fas fa-eye'"></i>
                </button>
              </div>
            </div>

            <!-- Sign In Button -->
            <button 
              type="submit" 
              class="btn btn-primary btn-full btn-large"
              [disabled]="!loginForm.valid || isLoading">
              <span *ngIf="!isLoading">Sign In</span>
              <span *ngIf="isLoading">
                <i class="fas fa-spinner fa-spin mr-2"></i>
                Signing in...
              </span>
            </button>
          </form>

          <!-- Divider -->
          <div class="relative my-6">
            <div class="absolute inset-0 flex items-center">
              <div class="w-full border-t border-gray-300"></div>
            </div>
            <div class="relative flex justify-center text-sm">
              <span class="px-2 bg-white text-gray-500">OR CONTINUE WITH</span>
            </div>
          </div>

          <!-- Social Login -->
          <div class="grid grid-cols-2 gap-3">
            <button 
              type="button" 
              class="btn btn-secondary flex items-center justify-center"
              (click)="signInWithGoogle()">
              <i class="fab fa-google mr-2 text-red-500"></i>
              Google
            </button>
            <button 
              type="button" 
              class="btn btn-secondary flex items-center justify-center"
              (click)="signInWithGitHub()">
              <i class="fab fa-github mr-2"></i>
              GitHub
            </button>
          </div>

          <!-- Sign Up Link -->
          <div class="text-center mt-6">
            <p class="text-gray-600">
              Don't have an account? 
              <a routerLink="/register" class="text-primary font-medium hover:text-primary-dark">
                Sign up
              </a>
            </p>
          </div>
        </div>

        <!-- Footer -->
        <div class="text-center text-sm text-gray-500">
          By continuing, you agree to our
          <a href="#" class="text-primary hover:text-primary-dark">Terms of Service</a>
          and
          <a href="#" class="text-primary hover:text-primary-dark">Privacy Policy</a>
        </div>
      </div>
    </div>
  `,
  styles: [`
    .input-group {
      position: relative;
    }

    .absolute {
      position: absolute;
    }

    .right-3 {
      right: 0.75rem;
    }

    .top-1\/2 {
      top: 50%;
    }

    .transform {
      transform: translateY(-50%);
    }

    .-translate-y-1\/2 {
      transform: translateY(-50%);
    }

    .space-y-8 > * + * {
      margin-top: 2rem;
    }

    .space-x-4 > * + * {
      margin-left: 1rem;
    }

    .text-red-500 {
      color: #ef4444;
    }

    .border-gray-300 {
      border-color: #d1d5db;
    }

    .border-t {
      border-top-width: 1px;
    }

    .inset-0 {
      top: 0;
      right: 0;
      bottom: 0;
      left: 0;
    }

    .relative {
      position: relative;
    }

    .px-2 {
      padding-left: 0.5rem;
      padding-right: 0.5rem;
    }

    .gap-3 {
      gap: 0.75rem;
    }

    @media (min-width: 640px) {
      .sm\\:px-6 {
        padding-left: 1.5rem;
        padding-right: 1.5rem;
      }
    }

    @media (min-width: 1024px) {
      .lg\\:px-8 {
        padding-left: 2rem;
        padding-right: 2rem;
      }
    }
  `]
})
export class LoginComponent {
  credentials = {
    email: '',
    password: ''
  };
  
  showPassword = false;
  isLoading = false;

  togglePassword() {
    this.showPassword = !this.showPassword;
  }

  onSubmit() {
    if (this.credentials.email && this.credentials.password) {
      this.isLoading = true;
      
      // Simulate API call
      setTimeout(() => {
        this.isLoading = false;
        console.log('Login submitted:', this.credentials);
        // Handle login logic here
      }, 2000);
    }
  }

  signInWithGoogle() {
    console.log('Sign in with Google');
    // Handle Google OAuth
  }

  signInWithGitHub() {
    console.log('Sign in with GitHub');
    // Handle GitHub OAuth
  }
}
