import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { RouterModule, Router } from '@angular/router';
import { ApiService } from '../../services/api.service';
import { AuditLoggerService } from '../../services/audit-logger.service';

@Component({
  selector: 'app-dashboard',
  standalone: true,
  imports: [CommonModule, RouterModule],
  template: `
    <div class="min-h-screen bg-gray-50">
      <!-- Header -->
      <header class="bg-white shadow-sm border-b border-gray-200">
        <div class="container mx-auto px-4 py-4">
          <div class="flex justify-between items-center">
            <div class="flex items-center">
              <div class="w-8 h-8 bg-primary rounded-lg flex items-center justify-center mr-3">
                <i class="fas fa-calendar-alt text-white text-lg"></i>
              </div>
              <span class="text-xl font-semibold text-gray-900">LeaveFlow</span>
            </div>
            <div class="flex items-center space-x-4">
              <span class="text-gray-600">Welcome, Employee</span>
              <button class="btn btn-secondary" routerLink="/home">
                <i class="fas fa-sign-out-alt mr-2"></i>
                Logout
              </button>
            </div>
          </div>
        </div>
      </header>

      <!-- Main Content -->
      <main class="container mx-auto px-4 py-8">
        <!-- Leave Balance Cards -->
        <div class="grid grid-cols-1 md:grid-cols-3 gap-6 mb-8">
          <div class="card">
            <div class="flex items-center justify-between">
              <div>
                <h3 class="text-lg font-semibold text-gray-900">Annual Leave</h3>
                <p class="text-3xl font-bold text-primary">{{ leaveBalances.annual }}</p>
                <p class="text-sm text-gray-500">days remaining</p>
              </div>
              <div class="w-12 h-12 bg-blue-100 rounded-lg flex items-center justify-center">
                <i class="fas fa-umbrella-beach text-blue-600 text-xl"></i>
              </div>
            </div>
          </div>

          <div class="card">
            <div class="flex items-center justify-between">
              <div>
                <h3 class="text-lg font-semibold text-gray-900">Sick Leave</h3>
                <p class="text-3xl font-bold text-warning">{{ leaveBalances.sick }}</p>
                <p class="text-sm text-gray-500">days remaining</p>
              </div>
              <div class="w-12 h-12 bg-red-100 rounded-lg flex items-center justify-center">
                <i class="fas fa-thermometer-half text-red-600 text-xl"></i>
              </div>
            </div>
          </div>

          <div class="card">
            <div class="flex items-center justify-between">
              <div>
                <h3 class="text-lg font-semibold text-gray-900">Casual Leave</h3>
                <p class="text-3xl font-bold text-success">{{ leaveBalances.casual }}</p>
                <p class="text-sm text-gray-500">days remaining</p>
              </div>
              <div class="w-12 h-12 bg-green-100 rounded-lg flex items-center justify-center">
                <i class="fas fa-coffee text-green-600 text-xl"></i>
              </div>
            </div>
          </div>
        </div>

        <!-- Quick Actions -->
        <div class="card mb-8">
          <h2 class="text-xl font-semibold text-gray-900 mb-4">Quick Actions</h2>
          <div class="grid grid-cols-1 md:grid-cols-2 gap-4">
            <button class="btn btn-primary btn-large">
              <i class="fas fa-plus mr-2"></i>
              Apply for Leave
            </button>
            <button class="btn btn-outline btn-large">
              <i class="fas fa-history mr-2"></i>
              View Leave History
            </button>
          </div>
        </div>

        <!-- Recent Leave Requests -->
        <div class="card">
          <h2 class="text-xl font-semibold text-gray-900 mb-4">Recent Leave Requests</h2>
          <div class="space-y-4">
            <div *ngFor="let leave of recentLeaves" class="flex items-center justify-between p-4 bg-gray-50 rounded-lg">
              <div>
                <h3 class="font-medium text-gray-900">{{ leave.type }}</h3>
                <p class="text-sm text-gray-500">{{ leave.startDate }} - {{ leave.endDate }}</p>
                <p class="text-sm text-gray-600">{{ leave.reason }}</p>
              </div>
              <div class="text-right">
                <span [class]="getStatusClass(leave.status)" class="px-3 py-1 rounded-full text-sm font-medium">
                  {{ leave.status }}
                </span>
                <p class="text-sm text-gray-500 mt-1">{{ leave.duration }} days</p>
              </div>
            </div>
          </div>
        </div>
      </main>
    </div>
  `,
  styles: [`
    .text-warning {
      color: var(--warning-color);
    }
    
    .text-success {
      color: var(--success-color);
    }

    .space-y-4 > * + * {
      margin-top: 1rem;
    }

    .space-x-4 > * + * {
      margin-left: 1rem;
    }

    .bg-blue-100 {
      background-color: rgba(59, 130, 246, 0.1);
    }

    .bg-red-100 {
      background-color: rgba(239, 68, 68, 0.1);
    }

    .bg-green-100 {
      background-color: rgba(16, 185, 129, 0.1);
    }

    .text-blue-600 {
      color: #2563eb;
    }

    .text-red-600 {
      color: #dc2626;
    }

    .text-green-600 {
      color: #059669;
    }

    .bg-gray-50 {
      background-color: #f9fafb;
    }
  `]
})
export class DashboardComponent {
  leaveBalances = {
    annual: 25,
    sick: 10,
    casual: 5
  };

  recentLeaves = [
    {
      type: 'Annual Leave',
      startDate: '2024-01-15',
      endDate: '2024-01-17',
      duration: 3,
      reason: 'Family vacation',
      status: 'APPROVED'
    },
    {
      type: 'Sick Leave',
      startDate: '2024-01-10',
      endDate: '2024-01-10',
      duration: 1,
      reason: 'Doctor appointment',
      status: 'PENDING'
    }
  ];

  getStatusClass(status: string): string {
    switch (status) {
      case 'APPROVED':
        return 'bg-green-100 text-green-800';
      case 'REJECTED':
        return 'bg-red-100 text-red-800';
      case 'PENDING':
        return 'bg-yellow-100 text-yellow-800';
      default:
        return 'bg-gray-100 text-gray-800';
    }
  }
}
