      border-radius: 50%;
      background: rgba(255, 255, 255, 0.05);
      backdrop-filter: blur(1px);
      animation: float 12s ease-in-out infinite;
    }

    .shape-1 {
      width: 400px;
      height: 400px;
      top: -10%;
      left: -5%;
      animation-delay: 0s;
    }

    .shape-2 {
      width: 300px;
      height: 300px;
      top: 20%;
      right: -10%;
      animation-delay: 2s;
    }

    .shape-3 {
      width: 200px;
      height: 200px;
      top: 60%;
      left: -5%;
      animation-delay: 4s;
    }

    .shape-4 {
      width: 350px;
      height: 350px;
      bottom: -10%;
      right: 10%;
      animation-delay: 1s;
    }

    .shape-5 {
      width: 250px;
      height: 250px;
      top: 40%;
      left: 30%;
      animation-delay: 3s;
    }

    .shape-6 {
      width: 180px;
      height: 180px;
      bottom: 30%;
      left: 70%;
      animation-delay: 5s;
    }

    @keyframes float {
      0%, 100% {
        transform: translateY(0px) rotate(0deg);
        opacity: 0.3;
      }
      25% {
        transform: translateY(-20px) rotate(90deg);
        opacity: 0.5;
      }
      50% {
        transform: translateY(-40px) rotate(180deg);
        opacity: 0.4;
      }
      75% {
        transform: translateY(-20px) rotate(270deg);
        opacity: 0.6;
      }
    }

    /* Feature Cards */
    .feature-card {
      background: rgba(255, 255, 255, 0.95);
      backdrop-filter: blur(20px);
      border-radius: 1.5rem;
      padding: 2.5rem 2rem;
      border: 1px solid rgba(255, 255, 255, 0.2);
      transition: all 0.3s ease;
      position: relative;
      overflow: hidden;
    }

    .feature-card:before {
      content: '';
      position: absolute;
      top: 0;
      left: 0;
      right: 0;
      height: 1px;
      background: linear-gradient(90deg, transparent, rgba(255, 255, 255, 0.8), transparent);
    }

    .feature-card:hover {
