import { Component } from '@angular/core';
import { CommonModule } from '@angular/common';
import { RouterModule } from '@angular/router';

@Component({
  selector: 'app-admin-dashboard',
  standalone: true,
  imports: [CommonModule, RouterModule],
  template: `
    <div class="min-h-screen bg-gray-50">
      <!-- Header -->
      <header class="bg-white shadow-sm border-b border-gray-200">
        <div class="container mx-auto px-4 py-4">
          <div class="flex justify-between items-center">
            <div class="flex items-center">
              <div class="w-8 h-8 bg-primary rounded-lg flex items-center justify-center mr-3">
                <i class="fas fa-calendar-alt text-white text-lg"></i>
              </div>
              <span class="text-xl font-semibold text-gray-900">LeaveFlow Admin</span>
            </div>
            <div class="flex items-center space-x-4">
              <span class="text-gray-600">Welcome, Admin</span>
              <button class="btn btn-secondary" routerLink="/home">
                <i class="fas fa-sign-out-alt mr-2"></i>
                Logout
              </button>
            </div>
          </div>
        </div>
      </header>

      <!-- Main Content -->
      <main class="container mx-auto px-4 py-8">
        <!-- Overview Stats -->
        <div class="grid grid-cols-1 md:grid-cols-4 gap-6 mb-8">
          <div class="card">
            <div class="flex items-center justify-between">
              <div>
                <h3 class="text-lg font-semibold text-gray-900">Total Employees</h3>
                <p class="text-3xl font-bold text-primary">{{ stats.totalEmployees }}</p>
              </div>
              <div class="w-12 h-12 bg-blue-100 rounded-lg flex items-center justify-center">
                <i class="fas fa-users text-blue-600 text-xl"></i>
              </div>
            </div>
          </div>

          <div class="card">
            <div class="flex items-center justify-between">
              <div>
                <h3 class="text-lg font-semibold text-gray-900">Pending Requests</h3>
                <p class="text-3xl font-bold text-warning">{{ stats.pendingRequests }}</p>
              </div>
              <div class="w-12 h-12 bg-yellow-100 rounded-lg flex items-center justify-center">
                <i class="fas fa-clock text-yellow-600 text-xl"></i>
              </div>
            </div>
          </div>

          <div class="card">
            <div class="flex items-center justify-between">
              <div>
                <h3 class="text-lg font-semibold text-gray-900">Approved Today</h3>
                <p class="text-3xl font-bold text-success">{{ stats.approvedToday }}</p>
              </div>
              <div class="w-12 h-12 bg-green-100 rounded-lg flex items-center justify-center">
                <i class="fas fa-check text-green-600 text-xl"></i>
              </div>
            </div>
          </div>

          <div class="card">
            <div class="flex items-center justify-between">
              <div>
                <h3 class="text-lg font-semibold text-gray-900">On Leave Today</h3>
                <p class="text-3xl font-bold text-accent">{{ stats.onLeaveToday }}</p>
              </div>
              <div class="w-12 h-12 bg-cyan-100 rounded-lg flex items-center justify-center">
                <i class="fas fa-plane text-cyan-600 text-xl"></i>
              </div>
            </div>
          </div>
        </div>

        <!-- Charts and Analytics -->
        <div class="grid grid-cols-1 lg:grid-cols-2 gap-8 mb-8">
          <!-- Leave Types Chart -->
          <div class="card">
            <h2 class="text-xl font-semibold text-gray-900 mb-4">Leave Types Distribution</h2>
            <div class="h-64 bg-gray-50 rounded-lg flex items-center justify-center">
              <div class="text-center">
                <i class="fas fa-chart-pie text-4xl text-gray-400 mb-4"></i>
                <p class="text-gray-500">Chart.js Pie Chart</p>
                <p class="text-sm text-gray-400">Annual: 60%, Sick: 25%, Casual: 15%</p>
              </div>
            </div>
          </div>

          <!-- Monthly Trends -->
          <div class="card">
            <h2 class="text-xl font-semibold text-gray-900 mb-4">Monthly Leave Trends</h2>
            <div class="h-64 bg-gray-50 rounded-lg flex items-center justify-center">
              <div class="text-center">
                <i class="fas fa-chart-line text-4xl text-gray-400 mb-4"></i>
                <p class="text-gray-500">Chart.js Line Chart</p>
                <p class="text-sm text-gray-400">Leave trends over the last 12 months</p>
              </div>
            </div>
          </div>
        </div>

        <!-- Pending Approvals -->
