                  <td class="px-4 py-3 text-gray-600">{{ request.duration }} days</td>
                  <td class="px-4 py-3 text-gray-600">{{ request.startDate }} - {{ request.endDate }}</td>
                  <td class="px-4 py-3 text-gray-600">{{ request.reason }}</td>
                  <td class="px-4 py-3">
                    <div class="flex space-x-2">
                      <button class="btn btn-success btn-sm" (click)="approveRequest(request.id)">
                        <i class="fas fa-check mr-1"></i>
                        Approve
                      </button>
                      <button class="btn btn-error btn-sm" (click)="rejectRequest(request.id)">
                        <i class="fas fa-times mr-1"></i>
                        Reject
                      </button>
                    </div>
                  </td>
                </tr>
              </tbody>
            </table>
          </div>
        </div>

        <!-- Recent Activity -->
        <div class="card">
          <h2 class="text-xl font-semibold text-gray-900 mb-4">Recent Activity</h2>
          <div class="space-y-4">
            <div *ngFor="let activity of recentActivity" class="flex items-center p-4 bg-gray-50 rounded-lg">
              <div class="w-10 h-10 bg-primary rounded-full flex items-center justify-center mr-4">
                <i [class]="activity.icon" class="text-white"></i>
              </div>
              <div class="flex-1">
                <p class="font-medium text-gray-900">{{ activity.description }}</p>
                <p class="text-sm text-gray-500">{{ activity.timestamp }}</p>
              </div>
            </div>
          </div>
        </div>
      </main>
    </div>
  `,
