import { Component } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormsModule } from '@angular/forms';
import { RouterModule } from '@angular/router';

@Component({
  selector: 'app-login',
  standalone: true,
  imports: [CommonModule, FormsModule, RouterModule],
    template: `
    <div class="min-h-screen flex items-center justify-center py-12 px-4 relative overflow-hidden">
      <!-- Animated Background Elements -->
      <div class="absolute inset-0 overflow-hidden">
        <div class="floating-shapes">
          <div class="shape shape-1"></div>
          <div class="shape shape-2"></div>
          <div class="shape shape-3"></div>
          <div class="shape shape-4"></div>
        </div>
      </div>

      <div class="max-w-md w-full space-y-8 relative z-10 fade-in-up">
        <!-- Back to home link -->
        <div class="text-left">
          <button 
            class="flex items-center text-white/80 hover:text-white transition-all duration-300 backdrop-blur-sm bg-white/10 rounded-full px-4 py-2"
            routerLink="/home">
            <i class="fas fa-arrow-left mr-2"></i>
            Back to home
          </button>
        </div>

        <!-- Logo and Title -->
        <div class="text-center fade-in">
          <div class="flex items-center justify-center mb-8">
            <div class="w-16 h-16 bg-gradient-to-br from-white/20 to-white/10 backdrop-blur-lg rounded-2xl flex items-center justify-center mr-4 shadow-xl border border-white/20">
              <i class="fas fa-calendar-alt text-white text-2xl"></i>
            </div>
            <div>
              <h1 class="text-4xl font-bold text-white tracking-tight">LeaveFlow</h1>
              <p class="text-white/80 text-sm font-medium">Smart Leave Management</p>
            </div>
          </div>
        </div>

        <!-- Login Form Card -->
        <div class="card fade-in-up">
          <div class="card-header">
            <h2 class="card-title">Welcome back</h2>
            <p class="card-subtitle">Sign in to manage your team's leave requests efficiently</p>
          </div>

          <form (ngSubmit)="onSubmit()" #loginForm="ngForm" class="space-y-6">
            <!-- Email -->
            <div class="form-group">
              <label class="form-label">Email Address</label>
              <div class="input-group">
                <i class="input-icon fas fa-envelope"></i>
                <input 
                  type="email" 
                  class="form-control"
                  placeholder="Enter your email address"
                  [(ngModel)]="credentials.email"
                  name="email"
                  required
                  email
                  autocomplete="email">
              </div>
            </div>

            <!-- Password -->
            <div class="form-group">
              <div class="flex justify-between items-center mb-2">
                <label class="form-label">Password</label>
                <button type="button" class="text-primary text-sm font-medium hover:text-primary-dark transition-colors">
                  Forgot password?
                </button>
              </div>
              <div class="input-group">
                <i class="input-icon fas fa-lock"></i>
                <input 
                  [type]="showPassword ? 'text' : 'password'" 
                  class="form-control"
                  placeholder="Enter your password"
                  [(ngModel)]="credentials.password"
                  name="password"
                  required
                  autocomplete="current-password">
                <button 
                  type="button"
                  class="absolute right-3 top-1/2 transform -translate-y-1/2 text-gray-400 hover:text-primary transition-colors"
                  (click)="togglePassword()">
                  <i [class]="showPassword ? 'fas fa-eye-slash' : 'fas fa-eye'"></i>
                </button>
              </div>
            </div>

            <!-- Remember Me -->
            <div class="flex items-center justify-between">
              <label class="flex items-center">
                <input type="checkbox" class="rounded border-gray-300 text-primary focus:ring-primary mr-3">
                <span class="text-sm text-gray-600">Remember me</span>
              </label>
            </div>

            <!-- Sign In Button -->
            <button 
              type="submit" 
              class="btn btn-primary btn-full btn-large group"
              [disabled]="!loginForm.valid || isLoading">
              <span *ngIf="!isLoading" class="flex items-center justify-center">
                <i class="fas fa-sign-in-alt mr-2 group-hover:translate-x-1 transition-transform"></i>
                Sign In
              </span>
              <span *ngIf="isLoading" class="flex items-center justify-center">
                <i class="fas fa-spinner fa-spin mr-2"></i>
                Signing in...
              </span>
            </button>
          </form>

          <!-- Sign Up Link -->
          <div class="text-center mt-8 pt-6 border-t border-gray-200">
            <p class="text-gray-600">
              Don't have an account? 
              <a routerLink="/register" class="text-primary font-semibold hover:text-primary-dark transition-colors ml-1">
                Create one now
              </a>
            </p>
          </div>
        </div>

        <!-- Footer -->
        <div class="text-center text-sm text-white/70 fade-in">
          <p class="mb-2">Secure & trusted by teams worldwide</p>
          <div class="flex justify-center space-x-4">
            <a href="#" class="hover:text-white transition-colors">Privacy Policy</a>
            <span>•</span>
            <a href="#" class="hover:text-white transition-colors">Terms of Service</a>
          </div>
        </div>
      </div>
    </div>
  `,
  styles: [`
    .input-group {
      position: relative;
    }

    .absolute {
      position: absolute;
    }

    .right-3 {
      right: 0.75rem;
    }

    .top-1\/2 {
      top: 50%;
    }

    .transform {
      transform: translateY(-50%);
    }

    .-translate-y-1\/2 {
      transform: translateY(-50%);
    }

    .space-y-8 > * + * {
      margin-top: 2rem;
    }

    .space-x-4 > * + * {
      margin-left: 1rem;
    }

    .text-red-500 {
      color: #ef4444;
    }

    .border-gray-300 {
      border-color: #d1d5db;
    }

    .border-t {
      border-top-width: 1px;
    }

    .inset-0 {
      top: 0;
      right: 0;
      bottom: 0;
      left: 0;
    }

    .relative {
      position: relative;
    }

    .px-2 {
      padding-left: 0.5rem;
      padding-right: 0.5rem;
    }

    .gap-3 {
      gap: 0.75rem;
    }

    @media (min-width: 640px) {
      .sm\\:px-6 {
        padding-left: 1.5rem;
        padding-right: 1.5rem;
      }
    }

    @media (min-width: 1024px) {
      .lg\\:px-8 {
        padding-left: 2rem;
        padding-right: 2rem;
      }
    }
  `]
})
export class LoginComponent {
  credentials = {
    email: '',
    password: ''
  };
  
  showPassword = false;
  isLoading = false;

  togglePassword() {
    this.showPassword = !this.showPassword;
  }

  onSubmit() {
    if (this.credentials.email && this.credentials.password) {
      this.isLoading = true;
      
      // Simulate API call
      setTimeout(() => {
        this.isLoading = false;
        console.log('Login submitted:', this.credentials);
        // Handle login logic here
      }, 2000);
    }
  }


}
